// src/components/AuthForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AuthForm.css'; // We'll create this CSS file

function AuthForm({ isRegistering, onLogin, onRegister }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(''); // Clear any previous errors

    console.log(isRegistering ? 'Registering with:' : 'Logging in with:', { email, password });

    if (isRegistering) {
      // Placeholder: Call the onRegister prop (which is our login function for now)
      if (onRegister) {
        onRegister();
        console.log('Navigating to My COOK Page after registration (placeholder)');
        navigate('/mycook');
      }
    } else {
      // Placeholder: Call the onLogin prop
      if (onLogin) {
        onLogin();
        console.log('Navigating to Select Meal after login (placeholder)');
        navigate('/select-meal');
      }
    }
  };

  return (
    <div className="auth-form">
      <h2>{isRegistering ? 'Register' : 'Login'}</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
      </form>
    </div>
  );
}

export default AuthForm;
